const apiKey = 'YOUR_API_KEY';
const url = 'https://api.smooth.sh/api/v1/task';

const payload = {
  task: 'Go to hacker news and get the top 5 stories.',
  device: 'desktop',
  max_steps: 32,
  enable_recording: true
};

async function runTask() {
  try {
    const response = await fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'apikey': apiKey
      },
      body: JSON.stringify(payload)
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();
    console.log('Task submitted:', data);
    // You can now use data.r.id to poll the get task endpoint
  } catch (error) {
    console.error('Error running task:', error);
  }
}

runTask();

const apiKey = 'YOUR_API_KEY';
const taskId = 'YOUR_TASK_ID';
const url = `https://api.smooth.sh/api/v1/task/${taskId}`;

async function getTaskStatus() {
  try {
    const response = await fetch(url, {
      method: 'GET',
      headers: {
        'apikey': apiKey
      }
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();
    console.log('Task status:', data);

    // You can poll this endpoint until status is 'done' or 'failed'
    if (data.r.status === 'running' || data.r.status === 'waiting') {
      console.log('Task is still in progress...');
    }

  } catch (error) {
    console.error('Error fetching task status:', error);
  }
}

getTaskStatus();

