const apiKey = 'YOUR_API_KEY';
const url = 'https://api.smooth.sh/api/v1/browser/session';

// To create a new session with a random ID, omit the payload or send an empty object
const payload = {
  session_id: 'my-persistent-session' // or null to create a new one
};

async function openSession() {
  try {
    const response = await fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'apikey': apiKey
      },
      body: JSON.stringify(payload)
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();
    console.log('Session opened:', data);
    console.log('Live URL:', data.r.live_url);
  } catch (error) {
    console.error('Error opening session:', error);
  }
}

openSession();

const apiKey = 'YOUR_API_KEY';
const sessionId = 'my-session'; // The ID of the session to close
const url = `https://api.smooth.sh/api/v1/browser/session/${sessionId}`;

async function closeSession() {
  try {
    const response = await fetch(url, {
      method: 'DELETE',
      headers: {
        'apikey': apiKey
      }
    });

    if (response.ok) {
        console.log(`Session '${sessionId}' closed successfully.`);
    } else {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

  } catch (error) {
    console.error('Error closing session:', error);
  }
}

closeSession();

